mapper.select:
/* batch.getHadoopFileList */ SELECT AA.* FROM ( SELECT A.cntrl_hstry_sn ,A.wrter_id ,B.sittn_reprt_sn ,B.sittn_reprt_no ,B.la ,B.lo ,B.content ,C.mngno ,C.mssage_cn ,C.mssage_writng_dt ,C.mssage_se_code ,C.writng_dept_code ,A.fileid ,C.filename ,C.hdfs_file_stre_cours_nm ,B.last_updt_dt FROM st_file_cs_ism_001.st_t_attachfileload A ,st_file_cs_ism_001.st_t_reprtfileload B ,st_file_cs_ism_002.st_t_attachfilecollect C WHERE 1=1 AND B.atchmnfl_id is not null AND B.atchmnfl_id = A.fileid AND A.mngno = C.mngno
lower(C.filename) LIKE CONCAT('%', #{item})
AND cast(B.last_updt_dt as timestamp) > cast(#{maxLastUpdtDt} as timestamp)
) AA

mapper.insert:
/* batch.insertImgVideoInfo */
	INSERT INTO kcgbd.st_t_hadoop_attachfile (
		cntrl_hstry_sn
		,wrter_id
		,content
		,sittn_reprt_sn
		,sittn_reprt_no
		,la
		,lo
		,mngno
		,mssage_cn
		,mssage_writng_dt
		,mssage_se_code
		,writng_dept_code
		,fileid
		,filename
		,hdfs_file_stre_cours_nm
		,last_updt_dt
		,trans_yn
		,dn_cnt
		,view_cnt
	) VALUES (
		#{cntrl_hstry_sn}
		,#{wrter_id}
		,#{content}
		,#{sittn_reprt_sn}
		,#{sittn_reprt_no}
		,#{la}
		,#{lo}
		,#{mngno}
		,#{mssage_cn}
		,#{mssage_writng_dt}
		,#{mssage_se_code}
		,#{writng_dept_code}
		,#{fileid}
		,#{filename}
		,#{hdfs_file_stre_cours_nm}
		,#{last_updt_dt}
		,'N'
		,0
		,0
	)

mapper.update:
/* batch.updateHadoopFileStorInfo */
	UPDATE kcgbd.st_t_hadoop_attachfile SET
		trans_yn = 'Y'
		,stor_file_nm = #{stor_file_nm}
	WHERE 1=1
		AND hdfs_file_stre_cours_nm = #{hdfs_file_stre_cours_nm}
