mapper.insert:
/* common.insertFileInfo */
	INSERT INTO kcgbd.t_cmmn_file_info (
		stor_file_nm
		,file_ext
		,upload_path
		,path
		,org_file_nm
		,file_size
		,content_type
	) VALUES (
		#{stor_file_nm}
		,#{file_ext}
		,#{upload_path}
		,#{path}
		,#{org_file_nm}
		,#{file_size}
		,#{content_type}
	)

mapper.update:
/* common.deleteFileInfo */
	UPDATE kcgbd.t_cmmn_file_info SET
		del_yn = 'y'
	WHERE 1=1
		AND stor_file_nm = #{stor_file_nm}

mapper.select:
/* common.getStatisticCnt */
	SELECT
		${col_nm} cnt
	FROM kcgbd.t_statistic
	WHERE 1=1
		AND statistic_date = #{statistic_date}
		AND user_id = #{user_id}
	LIMIT 1

mapper.insert:
/* common.insertStatisticCnt */
	INSERT INTO kcgbd.t_statistic (
		statistic_date
		,${col_nm}
		,user_id
	) VALUES (
		#{statistic_date}
		,${col_nm_cnt}
		,#{user_id}
	)

mapper.update:
/* common.updateStatisticCnt */
	UPDATE kcgbd.t_statistic SET
		${col_nm} = ${col_nm_cnt}
	WHERE 1=1
		AND statistic_date = #{statistic_date}
		AND user_id = #{user_id}

mapper.select:
/* common.getKeywordCnt */
	SELECT
		keyword_cnt
	FROM
		kcgbd.t_keyword_log
	WHERE 1=1
		AND keyword = #{keyword}
		AND reg_date = #{reg_date}

mapper.insert:
/* common.insertKeywordCnt */
	INSERT INTO kcgbd.t_keyword_log (
		keyword
		,keyword_cnt
		,reg_date
	) VALUES (
		#{keyword}
		,1
		,#{reg_date}
	)

mapper.update:
/* common.updateKeywordCnt */
	UPDATE kcgbd.t_keyword_log SET
		keyword_cnt = ${keyword_cnt}
	WHERE 1=1
		AND keyword = #{keyword}
		AND reg_date = #{reg_date}

/* common.addReadCntBigdataTrend */
	UPDATE kcgbd.t_bigdata_trend
	SET 
		read_cnt = (coalesce(read_cnt,0) + 1)
	WHERE idx = #{idx}

/* common.addReadCntInquire */
	UPDATE kcgbd.t_inquire
	SET 
		read_cnt = (coalesce(read_cnt,0) + 1)
	WHERE idx = #{idx}

/* common.addReadCntLibrary */
	UPDATE kcgbd.t_library
	SET 
		read_cnt = (coalesce(read_cnt,0) + 1)
	WHERE idx = #{idx}

/* common.addReadCntNews */
	UPDATE kcgbd.t_news
	SET 
		read_cnt = (coalesce(read_cnt,0) + 1)
	WHERE idx = #{idx}

/* common.addReadCntAnalIdea */
	UPDATE kcgbd.t_anal_idea
	SET 
		read_cnt = (coalesce(read_cnt,0) + 1)
	WHERE idx = #{idx}

/* common.addReadCntDataVisualization */
	UPDATE kcgbd.t_data_visualization
	SET 
		read_cnt = (coalesce(read_cnt,0) + 1)
	WHERE idx = #{idx}

/* common.addReadCntUseExample */
	UPDATE kcgbd.t_use_sample
	SET 
		read_cnt = (coalesce(read_cnt,0) + 1)
	WHERE idx = #{idx}

mapper.insert:
/* common.insertReqNoti */
	INSERT INTO kcgbd.t_req_noti (
		target_idx
		,target_cat
		,target_user_id
		,reg_dt
	) VALUES (
		#{target_idx}
		,#{target_cat}
		,#{target_user_id}
		,now()
	)

mapper.delete:
/* common.deleteNotiInfo */
	DELETE FROM kcgbd.t_req_noti
	WHERE 1=1
		AND target_idx = #{target_idx}
