mapper.select:
/* communi.mng.inquire.getList */ SELECT A.idx ,A.sbjt ,A.atch_file_1 ,(SELECT org_file_nm FROM kcgbd.t_cmmn_file_info WHERE stor_file_nm = A.atch_file_1) atch_file_1_nm ,A.atch_file_2 ,(SELECT org_file_nm FROM kcgbd.t_cmmn_file_info WHERE stor_file_nm = A.atch_file_2) atch_file_2_nm ,A.atch_file_3 ,(SELECT org_file_nm FROM kcgbd.t_cmmn_file_info WHERE stor_file_nm = A.atch_file_3) atch_file_3_nm ,A.atch_file_4 ,(SELECT org_file_nm FROM kcgbd.t_cmmn_file_info WHERE stor_file_nm = A.atch_file_4) atch_file_4_nm ,A.atch_file_5 ,(SELECT org_file_nm FROM kcgbd.t_cmmn_file_info WHERE stor_file_nm = A.atch_file_5) atch_file_5_nm ,A.repl ,A.ask_sts ,CASE WHEN A.ask_sts = '1' THEN '문의중' ELSE '답변완료' END AS ask_sts_nm ,A.read_cnt ,A.reg_dt ,A.updt_dt ,to_char(A.reg_dt, 'yyyy-mm-dd hh24:mi') reg_dt_char ,to_char(A.updt_dt, 'yyyy-mm-dd hh24:mi') updt_dt_char ,A.reg_user_id ,USR.name reg_user_name FROM kcgbd.t_inquire A ,kcgbd.t_user USR WHERE 1=1 AND USR.user_id = A.reg_user_id
AND A.SBJT like concat('%',#{search_val},'%')
AND USR.name like concat('%',#{search_val},'%')

mapper.update:
/* communi.mng.inquire.updateInfo */
	UPDATE kcgbd.t_inquire
	SET 
		sbjt = #{sbjt}
		,ctnt = #{ctnt}
		,atch_file_1 = #{atch_file_1}
		,atch_file_2 = #{atch_file_2}
		,atch_file_3 = #{atch_file_3}
		,atch_file_4 = #{atch_file_4}
		,atch_file_5 = #{atch_file_5}
		,repl = #{repl}
		,ask_sts = #{ask_sts}
		,updt_dt = NOW()
		,updt_user_id = #{user_id}
	WHERE idx = #{idx}

mapper.delete:
/* communi.mng.inquire.deleteInfo */
	DELETE FROM kcgbd.t_inquire
	WHERE idx = #{idx}
