mapper.select:
/* system.cust_mng.getList */ select B.CUST_NM as CUST_NM ,SUBSTRING(B.RRNO,1,6) as RRNO ,B.CUST_MBL_TELNO as CUST_MBL_TELNO ,D.PIC_NM as PIC_NM ,(SELECT CODE_NM FROM kcgbd.TB_CODEMST WHERE CODE_CL = '133' AND CODE_NO = A.CUST_EVT_TY_CD) as CUST_EVT_TY_CD_NM ,A.AVDAY_YMD as AVDAY_YMD from kcgbd.TB_CUSTEVENT A ,kcgbd.TB_CUSTMST B ,kcgbd.TB_CUSTPICREL C ,kcgbd.TB_PICMST D WHERE 1=1 and A.CUST_MBL_TELNO = B.CUST_MBL_TELNO and B.CUST_MBL_TELNO = C.CUST_MBL_TELNO and C.PIC_MBL_TELNO = D.PIC_MBL_TELNO and B.CURR_STCD = '0' and C.CURR_STCD = '0' and D.CURR_STCD = '0'
AND A.CUST_EVT_TY_CD = #{cust_evt_ty_cd}
AND D.DEPT_NM = #{dept_nm}
and B.CUST_NM = #{cust_nm}
and TO_CHAR(A.WRT_DT,'YYYYMMDD') = #{wrt_dt}

/* system.cust_mng.getCustInfoList */ select A.CUST_NM as CUST_NM ,SUBSTRING(A.RRNO,1,6) as RRNO ,A.CUST_MBL_TELNO as CUST_MBL_TELNO ,(SELECT CODE_NM FROM kcgbd.TB_CODEMST WHERE CODE_CL = '140' AND CODE_NO = A.OCCP_TY_CD) as OCCP_TY_CD_NM ,A.CUST_ADDR as CUST_ADDR from kcgbd.TB_CUSTMST A ,kcgbd.TB_CUSTPICREL B ,kcgbd.TB_PICMST C WHERE 1=1 and A.CUST_MBL_TELNO = B.CUST_MBL_TELNO and B.PIC_MBL_TELNO = C.PIC_MBL_TELNO and A.CURR_STCD = '0' and B.CURR_STCD = '0' and C.CURR_STCD = '0'
AND C.PIC_NM = #{pic_nm}
AND C.DEPT_NM = #{dept_nm}
and A.CUST_NM = #{cust_nm}
and SUBSTRING(A.RRNO,1,6) = #{rrno}

mapper.update:
/* system.cust_mng.updatePicRoof */
	UPDATE 
		kcgbd.TB_CUSTPICREL 
	SET
		PIC_MBL_TELNO = #{pic_mbl_telno}
	WHERE 1=1
      AND CUST_MBL_TELNO = #{cust_mbl_telno}

mapper.select:
	/* system.cust_mng.getCustInfo */
		SELECT  A.CUST_NM                     as CUST_NM  
          FROM kcgbd.TB_CUSTMST A
         where 1=1
           and A.CURR_STCD      = '0'

      		and A.CUST_NM = #{cust_nm}

mapper.update:
/* system.cust_mng.updateCust */
	UPDATE 
		kcgbd.TB_CUSTMST 
	SET
		cust_nm  = #{cust_nm}
	   ,wrt_dt   = now()
	   ,rrno     = #{rrno}
	   ,cust_eml_addr = #{cust_eml_addr}
	   ,co_telno = #{co_telno}
	   ,cust_mbl_telno = #{cust_mbl_telno}
	   ,occp_ty_cd = #{occp_ty_cd}
	   ,cust_addr = #{cust_addr} 
	WHERE 1=1
	  AND CUST_MBL_TELNO = #{cust_mbl_telno}

/* system.cust_mng.updatePic */
	UPDATE 
		kcgbd.TB_PICMST 
	SET
		pic_nm  = #{pic_nm}
	   ,dept_nm     = #{dept_nm}
	   ,jbps_ty_cd = #{jbps_ty_cd}
	   ,pic_eml_addr = #{pic_eml_addr}
	   ,jncmp_ymd = #{jncmp_ymd}
	   ,etc_tsk_cn = #{etc_tsk_cn}
	   ,wrt_dt   = now() 
	WHERE 1=1
	  AND PIC_MBL_TELNO = #{pic_mbl_telno}

mapper.select:
	/* system.cust_mng.getPicName */
		SELECT  A.PIC_NM                     as PIC_NM  
          FROM kcgbd.TB_PICMST A
         where 1=1
           and A.CURR_STCD      = '0'

      		and A.PIC_NM = #{pic_nm}
