mapper.update:
/* etc.mng.external.system.url.updateInfo */
	UPDATE kcgbd.t_ext_sys_url_mng
	SET 
		sys_nm = #{sys_nm}
		,updt_dt = now()
		,updt_user_id = #{user_id}
	WHERE ext_url = #{ext_url}

mapper.delete:
/* etc.mng.external.system.url.deleteInfo */
	DELETE FROM kcgbd.t_ext_sys_url_mng
	WHERE ext_url = #{ext_url}
