mapper.update:
/* etc.mng.help.updateInfo */
	UPDATE kcgbd.t_help_mng
	SET 
		sbjt = #{sbjt}
		,cat = #{cat}
		,ctnt = #{ctnt}
		,updt_dt = NOW()
		,updt_user_id = #{user_id}
	WHERE idx = #{idx}

mapper.delete:
/* etc.mng.help.deleteInfo */
	DELETE FROM kcgbd.t_help_mng
	WHERE idx = #{idx}

