mapper.update:
/* system.poli_person_mng.code.updateCmmnCodeInfo */
	UPDATE kcgbd.t_poli_person_code_mng SET
		code_nm = #{code_nm}
		,use_yn = #{use_yn}
		,cat_nm_1 = #{cat_nm_1}
		,cat_nm_2 = #{cat_nm_2}
		,cat_nm_3 = #{cat_nm_3}
		,cat_nm_4 = #{cat_nm_4}
		,cat_nm_5 = #{cat_nm_5}
	WHERE 1=1
		AND code_idx = #{code_idx}

mapper.select:
/* system.poli_person_mng.code.getCdMngList */ SELECT pcm.code_grp_id ,pcm.code_idx ,pcm.code ,pcm.code_nm ,pcm.use_yn ,pcm.del_yn ,pcm.cat1 ,pcm.cat_nm_1 ,pcm.cat2 ,pcm.cat_nm_2 ,pcm.cat3 ,pcm.cat_nm_3 ,pcm.cat4 ,pcm.cat_nm_4 ,pcm.cat5 ,pcm.cat_nm_5 ,pcm.cat6 ,pcm.cat_nm_6 ,pcm.pt_idx FROM kcgbd.t_poli_person_code_mng pcm WHERE 1=1 AND pcm.code_grp_id = #{code_grp_id}
AND pcm.cat1 = #{cat1}
AND pcm.cat2 = #{cat2}
AND pcm.cat3 = #{cat3}
AND pcm.cat4 = #{cat4}
AND pcm.cat5 = #{cat5}
ORDER BY cat_nm_1, cat_nm_2, cat_nm_3, cat_nm_4, cat_nm_5, cat_nm_6

mapper.delete:
/* system.poli_person_mng.code.deleteInfo */
	DELETE FROM kcgbd.t_poli_person_code_mng
	WHERE 1=1
		AND code_idx = #{code_idx}
