mapper.select:
/* system.prod_mng.getlist */ select prod_cd , prod_nm , prod_ty_cd , (select code_nm from kcgbd.tb_codemst where code_cl = '121' and code_no = prod_ty_cd ) prod_ty_cd_nm , sbstg_ty_cd , (select code_nm from kcgbd.tb_codemst where code_cl = '100' and code_no = sbstg_ty_cd ) sbstg_ty_cd_nm , ntsl_amt_min , ntsl_amt_max , prod_air_min , prod_air_max , pay_ty_cd , (select code_nm from kcgbd.tb_codemst where code_cl = '110' and code_no = pay_ty_cd ) pay_ty_cd_nm , to_char(to_date(prod_air_bgng_ymd, 'yyyymmdd'), 'yyyy-mm-dd') prod_air_bgng_ymd , to_char(to_date(prod_air_end_ymd, 'yyyymmdd'), 'yyyy-mm-dd') prod_air_end_ymd , int_tax_ty_cd , (select code_nm from kcgbd.tb_codemst where code_cl = '131' and code_no = int_tax_ty_cd ) int_tax_ty_cd_nm , curr_stcd , to_char(to_date(ntsl_bgng_ymd, 'yyyymmdd'), 'yyyy-mm-dd') ntsl_bgng_ymd , to_char(to_date(ntsl_end_ymd, 'yyyymmdd'), 'yyyy-mm-dd') ntsl_end_ymd , wrter_nm , wrt_dt from kcgbd.tb_prodmst where 1=1
and prod_cd = #{prod_cd}
and prod_nm like concat('%',#{prod_nm},'%')
and sbstg_ty_cd = #{sbstg_ty_cd}
and pay_ty_cd = #{pay_ty_cd}
and #{from_date} between ntsl_bgng_ymd and ntsl_bgng_ymd
and prod_ty_cd = #{prod_ty_cd}

mapper.update:
/* system.prod_mng.updateinfo */
	update kcgbd.tb_prodmst
	   set prod_nm           = #{prod_nm}
	     , prod_ty_cd        = #{prod_ty_cd}
	     , sbstg_ty_cd       = #{sbstg_ty_cd}
	     , ntsl_amt_min      = cast(#{ntsl_amt_min} as numeric(15))
	     , ntsl_amt_max      = cast(#{ntsl_amt_max} as numeric(15))
	     , prod_air_min      = cast(#{prod_air_min} as numeric(3,2))
	     , prod_air_max      = cast(#{prod_air_max} as numeric(5,2))
	     , pay_ty_cd         = #{pay_ty_cd}
	     , prod_air_bgng_ymd = replace(#{prod_air_bgng_ymd}, '-', '')
	     , prod_air_end_ymd  = replace(#{prod_air_end_ymd}, '-', '')
	     , int_tax_ty_cd     = #{int_tax_ty_cd}
	     , curr_stcd         = #{curr_stcd}
	     , ntsl_bgng_ymd     = replace(#{ntsl_bgng_ymd}, '-', '')
	     , ntsl_end_ymd      = replace(#{ntsl_end_ymd}, '-', '')
	 where prod_cd = #{prod_cd}

mapper.delete:
/* system.prod_mng.deleteinfo */
	delete 
	  from kcgbd.tb_prodmst
	 where prod_cd = #{prod_cd}
