mapper.select:
/* req.mng.dnld.getList */ SELECT A.idx ,A.reg_user_id ,USR.name reg_user_name ,A.req_sts ,(SELECT code_nm FROM kcgbd.t_cmmn_code WHERE code_grp_id = 'APRVCD' AND code_id = A.req_sts) req_sts_nm ,A.cmnt ,A.reg_dt ,A.updt_dt ,to_char(A.reg_dt, 'yyyy-mm-dd hh24:mi') reg_dt_char ,to_char(A.updt_dt, 'yyyy-mm-dd hh24:mi') updt_dt_char ,A.table_eng_nm ,(SELECT count(*) FROM kcgbd.t_download_req_dtl WHERE download_req_idx = A.idx) req_col_cnt ,(SELECT count(*) FROM kcgbd.t_rule_meta_0003 RM3 WHERE RM3.db_table_id = A.db_table_id) tot_col_cnt ,( SELECT BR3.accumulated_records FROM kcgbd.t_batch_result_0003 BR3 ,( SELECT planned_dt FROM kcgbd.t_batch_date_0001 WHERE flag = 0 ORDER BY planned_dt DESC LIMIT 1 ) D WHERE 1=1 AND BR3.planned_dt = D.planned_dt AND BR3.cnnc_manage_no = RM2.cnnc_manage_no AND BR3.table_eng_nm = RM2.table_eng_nm ) accumulated_records ,RM1.sys_nm ,( SELECT planned_dt FROM kcgbd.t_batch_date_0001 WHERE flag = 0 ORDER BY planned_dt DESC LIMIT 1 ) planned_dt FROM kcgbd.t_download_req A ,kcgbd.t_rule_meta_0002 RM2 ,kcgbd.t_rule_meta_0001 RM1 ,kcgbd.t_user USR WHERE 1=1 AND A.db_table_id = RM2.db_table_id AND RM1.cnnc_manage_no = RM2.cnnc_manage_no AND USR.user_id = A.reg_user_id
AND RM1.sys_nm like concat('%',#{search_val},'%')
AND USR.name like concat('%',#{search_val},'%')

mapper.update:
/* req.mng.dnld.updateInfo */
	UPDATE 
		kcgbd.t_download_req 
	SET
		req_sts = #{req_sts}
		,cmnt = #{cmnt}
		,updt_user_id = #{user_id}
		,updt_dt = now()
	WHERE 1=1
		AND idx = #{idx}

mapper.delete:
/* req.mng.dnld.deleteInfo */
	DELETE FROM kcgbd.t_download_req
	WHERE idx = #{idx}

mapper.update:
/* req.mng.dnld.updtStatus */
	UPDATE 
		kcgbd.t_download_req 
	SET
		req_sts = #{req_sts}
		,updt_user_id = #{user_id}
		,updt_dt = now()
	WHERE 1=1
		AND idx = #{idx}
