mapper.insert:
/* system.admin_auth_mng.insertInfo_user */
	INSERT INTO kcgbd.t_user (
		user_id
		,auth_cd
		,iam_yn
		,name
		,del_yn
		,user_pw
		,reg_dt
	) VALUES (
		#{auth_nm}
		,#{auth_nm}
		,'N'
		,#{name}
		,'N'
		,#{user_pw}
		,now()
	)

/* system.admin_auth_mng.insertInfo_auth */
	INSERT INTO kcgbd.t_auth (
		auth_cd
		,auth_nm
		,admin_yn
		,reg_dt
		,updt_dt
		,reg_user_id
		,updt_user_id
	) VALUES (
		#{auth_nm}
		,#{auth_nm}
		,'Y'
		,now()
		,now()
		,#{user_id}
		,#{user_id}
	)

mapper.update:
/* system.admin_auth_mng.delete_user */
	UPDATE kcgbd.t_user SET
		del_yn = 'Y'
	WHERE 1=1
		AND user_id = #{auth_nm}

mapper.delete:
/* system.admin_auth_mng.delete_auth */
	DELETE FROM kcgbd.t_auth
	WHERE 1=1
		AND auth_nm = #{auth_nm}

/* system.admin_auth_mng.deleteAuthMenuMappingInfo */
	DELETE FROM kcgbd.t_auth_menu_mapping
	WHERE 1=1
		AND auth_cd = #{auth_nm}

mapper.update:
/* system.admin_auth_mng.initPw */
	UPDATE kcgbd.t_user SET
		user_pw = #{user_pw}
	WHERE 1=1
		AND user_id = #{auth_nm}
