mapper.update:
/* etc.mng.err.cd.updateInfo */
	UPDATE kcgbd.t_err_cd_mng
	SET 
		err_kor_nm = #{err_kor_nm}
		,err_eng_nm = #{err_eng_nm}
		,updt_dt = now()
		,updt_user_id = #{user_id}
	WHERE err_cd = #{err_cd}

mapper.delete:
/* etc.mng.err.cd.deleteInfo */
	DELETE FROM kcgbd.t_err_cd_mng
	WHERE err_cd = #{err_cd}
