mapper.select:
/* etc.mng.popup.getList */ SELECT A.idx ,A.pup_nm ,A.strt_dtm ,A.end_dtm ,A.left_pos ,A.top_pos ,A.width ,A.height ,A.prcs_yn ,CASE WHEN A.prcs_yn='Y' THEN '진행' ELSE '종료' END AS prcs_yn_nm ,A.reg_dt ,A.updt_dt ,to_char(A.reg_dt, 'yyyy-mm-dd hh24:mi') reg_dt_char ,to_char(A.updt_dt, 'yyyy-mm-dd hh24:mi') updt_dt_char ,A.reg_user_id ,USR.name reg_user_name FROM kcgbd.t_pup_mng A ,kcgbd.t_user USR WHERE 1=1 AND USR.user_id = A.reg_user_id
AND A.pup_nm like concat('%',#{search_val},'%')
AND USR.name like concat('%',#{search_val},'%')

mapper.update:
/* etc.mng.popup.updateInfo */
	UPDATE kcgbd.t_pup_mng 
	SET 
		pup_nm = #{pup_nm}
		,pup_ctnt = #{pup_ctnt}
		,strt_dtm = #{strt_dtm}
		,end_dtm = #{end_dtm}
		,left_pos = #{left_pos}
		,top_pos = #{top_pos}
		,width = #{width}
		,height = #{height}
		,prcs_yn = #{prcs_yn}
		,updt_dt = NOW()
		,updt_user_id = #{user_id}
	WHERE idx = #{idx}

mapper.delete:
/* etc.mng.popup.deleteInfo */
	DELETE FROM kcgbd.t_pup_mng
	WHERE idx = #{idx}
