mapper.select:
/* system.menu_mng.getList */ SELECT mnu.menu_url ,mnu.menu_nm ,mnu.menu_note ,mnu.admin_yn ,mnu.reg_user_id ,to_char(mnu.reg_dt, 'YYYY-MM-DD HH24:MI:SS.MS') reg_dt_char ,mnu.reg_dt FROM kcgbd.t_menu mnu WHERE 1=1
AND mnu.admin_yn = #{admin_yn}
AND mnu.menu_url like concat('%',#{search_val},'%')
AND mnu.menu_nm like concat('%',#{search_val},'%')

mapper.insert:
/* system.menu_mng.insertInfo */
	INSERT INTO kcgbd.t_menu(
		menu_url
		,menu_nm
		,menu_note
		,admin_yn
		,reg_user_id
		,reg_dt
	) VALUES (
		#{menu_url}
		,#{menu_nm}
		,#{menu_note}
		,#{admin_yn}
		,#{user_id}
		,now()
	)

mapper.update:
/* system.menu_mng.updateInfo */
	UPDATE kcgbd.t_menu SET
		menu_nm = #{menu_nm}
		,menu_note = #{menu_note}
		,admin_yn = #{admin_yn}
		,reg_user_id = #{user_id}
	WHERE 1=1
		AND menu_url = #{menu_url}

mapper.delete:
/* system.menu_mng.delete */
	DELETE FROM kcgbd.t_menu
	WHERE menu_url = #{menu_url}

mapper.select:
/* system.menu_mng.chkExist */
	SELECT
		count(*) cnt
	FROM kcgbd.t_menu mnu
	WHERE 1=1
		AND mnu.menu_url = #{menu_url}

mapper.delete:
/* system.menu_mng.deleteAuthMapping */
	DELETE FROM kcgbd.t_auth_menu_mapping
	WHERE menu_url = #{menu_url}
