mapper.select:
/* req.mng.data.collect.getList */ SELECT A.idx ,A.reg_user_id ,USR.name reg_user_name ,A.sbjt ,A.gbn ,(SELECT code_nm FROM kcgbd.t_cmmn_code WHERE code_grp_id = 'DTCOLCTREQ' AND code_id =A.gbn) gbn_nm ,A.atch_file_1 ,(SELECT org_file_nm FROM kcgbd.t_cmmn_file_info WHERE stor_file_nm = A.atch_file_1) atch_file_1_nm ,A.atch_file_2 ,(SELECT org_file_nm FROM kcgbd.t_cmmn_file_info WHERE stor_file_nm = A.atch_file_2) atch_file_2_nm ,A.atch_file_3 ,(SELECT org_file_nm FROM kcgbd.t_cmmn_file_info WHERE stor_file_nm = A.atch_file_3) atch_file_3_nm ,A.atch_file_4 ,(SELECT org_file_nm FROM kcgbd.t_cmmn_file_info WHERE stor_file_nm = A.atch_file_4) atch_file_4_nm ,A.atch_file_5 ,(SELECT org_file_nm FROM kcgbd.t_cmmn_file_info WHERE stor_file_nm = A.atch_file_5) atch_file_5_nm ,A.req_dtl_ctnt ,A.req_resn ,A.use_purp ,A.req_sts ,(SELECT code_nm FROM kcgbd.t_cmmn_code WHERE code_grp_id = 'APRVCD' AND code_id = A.req_sts) req_sts_nm ,A.cmnt ,A.reg_dt ,A.updt_dt ,to_char(A.reg_dt, 'yyyy-mm-dd hh24:mi') reg_dt_char ,to_char(A.updt_dt, 'yyyy-mm-dd hh24:mi') updt_dt_char ,A.etc_note ,A.gov_open_db_org ,A.gov_open_db_nm ,A.etc_act_db_org ,A.etc_act_db_nm FROM kcgbd.t_data_collect_req A ,kcgbd.t_user USR WHERE 1=1 AND USR.user_id = A.reg_user_id
AND A.SBJT like concat('%',#{search_val},'%')
AND USR.name like concat('%',#{search_val},'%')

mapper.update:
/* req.mng.data.collect.updateInfo */
	UPDATE 
		kcgbd.t_data_collect_req
	SET 
		req_sts = #{req_sts}
		,cmnt = #{cmnt}
		,updt_user_id = #{user_id}
		,updt_dt = now()
	WHERE 1=1
	 	AND idx= #{idx}

mapper.delete:
/* req.mng.data.collect.deleteInfo */
	DELETE FROM kcgbd.t_data_collect_req
	WHERE 1=1
	 	AND idx= #{idx}

mapper.update:
/* req.mng.data.collect.updtStatus */
	UPDATE 
		kcgbd.t_data_collect_req
	SET 
		req_sts = #{req_sts}
		,updt_user_id = #{user_id}
		,updt_dt = now()
	WHERE 1=1
	 	AND idx= #{idx}
