mapper.select:
/* system.user_mng.getList */ SELECT USR.user_id ,USR.auth_cd ,(SELECT auth_nm FROM kcgbd.t_auth WHERE auth_cd = USR.auth_cd ) auth_nm ,USR.iam_yn ,USR.status_cd ,(SELECT code_nm FROM kcgbd.t_cmmn_code WHERE code_id = USR.status_cd AND code_grp_id = 'STATUSCD' ) statusnm ,cc.code_nm AS statusnm ,USR.name ,USR.jikgub_cd ,USR.jikgub_nm ,USR.dept ,USR.tdept_nm ,USR.email ,auth.auth_nm ,USR.reg_dt ,USR.gis_auth ,to_char(USR.reg_dt, 'YYYY-MM-DD') reg_dt_char FROM kcgbd.t_user USR ,kcgbd.t_cmmn_code cc ,kcgbd.t_auth auth WHERE 1=1 AND cc.code_grp_id = 'STATUSCD' AND cc.code_id = USR.status_cd AND USR.del_yn = 'N' AND auth.auth_cd = USR.auth_cd AND auth.admin_yn = 'N'
AND USR.name like concat('%',#{search_val},'%')
AND USR.user_id like concat('%',#{search_val},'%')

mapper.insert:
/* system.user_mng.insertInfo */
	INSERT INTO kcgbd.t_user (
		user_id
		,auth_cd
		,iam_yn
		,status_cd
		,name
		,jikgub_cd
		,dept
		,jikgub_nm
		,tdept_nm
		,email
		,gis_auth
		,del_yn
		,user_pw
		,reg_dt
	) VALUES (
		#{user_id}
		,#{auth_cd}
		,#{iam_yn}
		,#{status_cd}
		,#{name}
		,#{jikgub_cd}
		,#{dept}
		,#{jikgub_nm}
		,#{tdept_nm}
		,#{email}
		,#{gis_auth}
		,'N'
		,#{user_pw}
		,now()
	)

mapper.update:
/* system.user_mng.updateInfo */
	UPDATE kcgbd.t_user SET
		user_id = #{user_id}
		,auth_cd = #{auth_cd}
		,iam_yn = #{iam_yn}
		,status_cd = #{status_cd}
		,name = #{name}
		,jikgub_cd = #{jikgub_cd}
		,dept = #{dept}
		,jikgub_nm = #{jikgub_nm}
		,tdept_nm = #{tdept_nm}
		,email = #{email}
		,gis_auth = #{gis_auth}
	WHERE 1=1
		AND user_id = #{user_id}

mapper.delete:
/* system.user_mng.deleteUserSystemMappingList */
	DELETE FROM kcgbd.t_user_system_mapping
	WHERE 1=1
		AND user_id = #{user_id}

mapper.insert:
/* system.user_mng.insertSystemMapping */
	INSERT INTO kcgbd.t_user_system_mapping(
		user_id
		,cnnc_manage_no
		,table_eng_nm
	) VALUES (
		#{user_id}
		,#{cnnc_manage_no}
		,#{table_eng_nm}
	)

mapper.update:
/* system.user_mng.delete */
	UPDATE kcgbd.t_user SET
		del_yn = 'Y'
	WHERE 1=1
		AND user_id = #{user_id}

/* system.user_mng.initPw */
	UPDATE kcgbd.t_user SET
		user_pw = #{user_pw}
	WHERE 1=1
		AND user_id = #{user_id}
